mapper.select:
select , user.name as user_name, cod.description as grade_desc from commissionsuspending record inner join xquark_user user on record.cpId = user.cpId left join gradeCode cod on cod.id = record.grade_id where user.cpId = #{cpId,jdbcType=BIGINT} and user.archive = false
and record.source = #{source,jdbcType=INTEGER}
order by record.createdDate desc
limit #{offset}, #{size}

select count(*)
from commissionsuspending record
inner join xquark_user user on record.cpId = user.cpId
where user.cpId = #{cpId,jdbcType=BIGINT}
and user.archive = false

  and record.source = #{source,jdbcType=INTEGER}

select

from commissionsuspending
where cpId = #{cpId,jdbcType=BIGINT}
and orderid = #{bizId,jdbcType=VARCHAR}
and rollbacked = false
and trancd = #{type,jdbcType=VARCHAR}
order by created_at desc

select exists(
  select 1
  from commissionsuspending
  where cpId = #{cpId,jdbcType=BIGINT}
        and orderid = #{bizId,jdbcType=VARCHAR}
        and trancd = #{type,jdbcType=VARCHAR}
)
