mapper.select:
select exists(
         select 1 from customerWithdrawal where commsuspendingId = #{susId,jdbcType=BIGINT}
         )

select cp.cpId as cp_id, cp.namecn as name, cp.phoneno1 as phone, cp.tincode as tincode, cb.bankaccount as bank_account, cb.bankfullname as bank_name, cb.banknumber as bank_number, SUM(cw.amount) as amount, CONCAT(cp.customerNumber, ';', cw.processingMonth) as remark from customerWithdrawal cw left join customerprofile cp on cw.cpId = cp.cpId left join customerbank cb on cw.cpId = cb.cpId where cp.isdeleted = false and cp.is_active = true and cb.bankfullname = '中国银行'
and cw.processingMonth = #{orderMonth,jdbcType=INTEGER}
and cp.source = #{source,jdbcType=INTEGER}
GROUP BY cp_id

select cp.cpId as cp_id, cp.namecn as name, cp.phoneno1 as phone, cp.tincode as tincode, cb.bankaccount as bank_account, cb.bankfullname as bank_name, cb.banknumber as bank_number, SUM(cw.amount) as amount, CONCAT(cp.customerNumber, ';', cw.processingMonth) as remark from customerWithdrawal cw left join customerprofile cp on cw.cpId = cp.cpId left join customerbank cb on cw.cpId = cb.cpId where cp.isdeleted = false and cp.is_active = true and cb.bankfullname != '中国银行'
and cw.processingMonth = #{orderMonth,jdbcType=INTEGER}
and cp.source = #{source,jdbcType=INTEGER}
GROUP BY cp_id

select cp.cpId as cp_id, cp.namecn as name, cp.phoneno1 as phone, cp.tincode as tincode, cb.bankaccount as bank_account, cb.bankfullname as bank_name, cb.banknumber as bank_number, SUM(cw.amount) as amount, CONCAT(cp.customerNumber, ';', cw.processingMonth) as remark from customerWithdrawal cw left join customerprofile cp on cw.cpId = cp.cpId left join customerbank cb on cw.cpId = cb.cpId where cp.isdeleted = false and cp.is_active = true
and cw.processingMonth = #{orderMonth,jdbcType=INTEGER}
and cp.source = #{source,jdbcType=INTEGER}
GROUP BY cp_id
